SELECT top 1 BizDate from @{DDITable}
where aggcode = '@{aggcode}'
@{user}
ORDER BY BizDate desc

SELECT a0.region, sum(Amount) as Amoumt , sum(preAmount) as preAmount, sum(Amount) - sum(preAmount)  as added  from (
SELECT monthno, region, sum(Amount) as Amount from agg_Purchase_o_pe_pr_r agg
LEFT JOIN md_peroid p on p.id = agg.peroid
WHERE aggcode = '@{aggcode}' and
@{filter}
@{user}
GROUP BY monthno, region
) a0
LEFT JOIN
(
SELECT monthno, region, sum(Amount) as preAmount from agg_Purchase_o_pe_pr_r agg
LEFT JOIN md_peroid p on p.id = agg.peroid
WHERE aggcode = '@{aggcode}' and
@{filter}
@{user}
GROUP BY monthno, region
)a1 on a0.region = a1.region and a0.monthno = a1.monthno + 12
WHERE a0.region is not null
GROUP BY a0.region

SELECT a0.region, a0.sumdata, a1.presumdata, a0.target, a0.achieve, (sumdata - presumdata) * 100/ CASE WHEN presumdata = 0 THEN 1 ELSE presumdata END as growth FROM( SELECT region, year, agg.@{userType}, sum(monthsum) as sumdata, sum(monthTarget) as target, ( ISNULL(SUM(monthsum), 0) * 100 / ISNULL(SUM(monthTarget), 1) ) AS achieve from VIEW_@{dataType}_Month_@{brandType} agg LEFT JOIN territory t on t.@{userType} = agg.@{userType} LEFT JOIN usr on usr.LoginName = t.RSM LEFT JOIN RSMRegion r on r.UserID = usr.UserID LEFT JOIN md_peroid p on p.id = agg.peroid
WHERE agg.@{userType} = '@{userid}' and Region is not null and @{filter} GROUP BY region,year, agg.@{userType} ) a0 LEFT JOIN ( SELECT region,year, agg.@{userType}, sum(monthsum) as presumdata from VIEW_Salesperson_Month_Total agg LEFT JOIN territory t on t.@{userType} = agg.@{userType} LEFT JOIN usr on usr.LoginName = t.RSM LEFT JOIN RSMRegion r on r.UserID = usr.UserID LEFT JOIN md_peroid p on p.id = agg.peroid
WHERE agg.@{userType} = '@{userid}' and Region is not null GROUP BY region, year, agg.@{userType} ) a1 on a0.region = a1.region and a0.@{userType} = a1.@{userType} and a0.year = a1.year + 1;

SELECT * from ( SELECT
d.DistributorName , sum(a0.Amount) / ISNULL(sum(a1.averageAmount) , 1) as inventoryday FROM ( SELECT BizDate,brand, DistributorCode, sum(Amount) as Amount from agg_Inventory_d_o_pe_pr WHERE aggcode = '@{aggcode}' and BizDate is not null and DistributorCode is not null and BizDate = '@{lastinventorydate}' @{brandfilter} @{user} GROUP BY BizDate, DistributorCode,brand )a0 LEFT JOIN( SELECT DistributorCode, brand, sum(Amount)/90 as averageAmount from VIEW_DataSales WHERE bizdate >=DATEADD(D,0,DATEDIFF(D,0,'@{lastinventorydate}')-90) and BizDate <= '@{lastinventorydate}' GROUP BY DistributorCode, brand )a1 on a0.DistributorCode = a1.DistributorCode and a0.brand = a1.brand LEFT JOIN Distributor d on a0.DistributorCode = d.DistributorCode
GROUP BY d.DistributorName )a01 ORDER BY inventoryday desc;

SELECT
sum(monthsum) as sumdata, SUM(monthTarget) as target,
sum(monthsum)*100 / ISNULL(sum(monthTarget), 1) as achieve  from(
SELECT LoginName, EnglishName from organization
WHERE ParentLoginName = '@{userid}'
and  LoginName is not null
)a0
LEFT JOIN VIEW_@{dataType}_Month_Total t on a0.LoginName = t.@{subType}
LEFT JOIN md_peroid p on p.id = t.peroid
WHERE @{filter}

SELECT a01.brand, count(a01.CompanyCode) as num, count(a02.CompanyCode) as prenum ,  count(a01.CompanyCode)  - count(a02.CompanyCode) as new
, overlap = (
SELECT count(1) from (
SELECT ag.CompanyCode from agg_Sale_c_o_pe_pr_r ag
LEFT JOIN md_peroid p on p.id = ag.peroid
LEFT JOIN Company c on c.CompanyCode = ag.CompanyCode
WHERE aggcode = 'brand-CompanyCode-peroid-region-RSM'
and [year] = @{year}
@{onlyuser}
and @{companyTypefilter} and @{regionfilter}
and ag.brand = 'Anthogyr'
INTERSECT
SELECT ag.CompanyCode from agg_Sale_c_o_pe_pr_r ag
LEFT JOIN md_peroid p on p.id = ag.peroid
LEFT JOIN Company c on c.CompanyCode = ag.CompanyCode
WHERE aggcode = 'brand-CompanyCode-peroid-region-RSM'
and [year] = @{year}
@{onlyuser}
and @{companyTypefilter} and @{regionfilter}
and ag.brand = 'Straumann'
) a22
)

from (
SELECT
brand, agg.CompanyCode

FROM [dbo].[agg_Sale_c_o_pe_pr_r] agg
LEFT JOIN md_peroid p on p.id = agg.peroid
LEFT JOIN Company c on c.CompanyCode = agg.CompanyCode
WHERE aggcode = 'brand-CompanyCode-peroid-region-RSM'
and [year] = @{year}
@{onlyuser}
and @{companyTypefilter} and @{regionfilter}

GROUP BY brand,agg.CompanyCode
)a01

LEFT JOIN
(
SELECT
brand, agg.CompanyCode

FROM [dbo].[agg_Sale_c_o_pe_pr_r] agg
LEFT JOIN md_peroid p on p.id = agg.peroid
LEFT JOIN Company c on c.CompanyCode = agg.CompanyCode
WHERE aggcode = 'brand-CompanyCode-peroid-region-RSM'
and [year] = @{year} - 1
@{onlyuser}
and @{companyTypefilter} and @{regionfilter}

GROUP BY brand, agg.CompanyCode
) a02
