update @{aggtable} set @{aggtable}.@{targetName} = m.monthTarget
from @{aggtable} agg LEFT JOIN md_peroid p on agg.peroid = p.id LEFT JOIN @{targetTableName} m on agg.brand = m.Brand and p.quarter = m.quarter and p.[year] = m.[Year] @{tagertjion}
WHERE agg.brand is not null and agg.peroid is not null @{targetfilter} and m.Category = '@{category}'
